(function($){$.fn.retina=function(){if((window.devicePixelRatio==2)||(screen.width>1920)){$('img').attr('src',function(e){var a="";var b=$(this).attr('src');b=b.split('.');var c=b.length;for(i=0;i<c;i++){if(i<c-1){a+=b[i]}else{a+="@2x."+b[i]}}var d=$(this);$.ajax({url:a,type:'HEAD',error:function(){},success:function(){d.attr('src',a)}})})}}})(jQuery);